            unchecked((byte) -1),
            unchecked((byte) -1),
            unchecked((byte) -1),
            unchecked((byte) -1),
            unchecked((byte) -1),
            unchecked((byte) -1),
            unchecked((byte) -1)
        ], 
        byteBuffer.GetBuffer().ToArray());
    }
    
