    [Fact]
    public void should_encode_short_string()
    {
        // Arrange
        var byteBuffer = new ByteBuffer();

        // Act
        byteBuffer.WriteString("abcdefgh");

        // Assert
        var expected = new byte[] { 0, 0, 0, 8, 0, 97, 0, 98, 0, 99, 0, 100, 0, 101, 0, 102, 0, 103, 0, 104 };
        Assert.Equal(expected, byteBuffer.GetBuffer().ToArray());
    }
    
    [Fact]
    public void should_decode_short_string()
    {
        // Arrange
        var byteBuffer = new ByteBuffer([0, 0, 0, 8, 0, 97, 0, 98, 0, 99, 0, 100, 0, 101, 0, 102, 0, 103, 0, 104]);

        // Act
        var value = byteBuffer.ReadString();

        // Assert
        Assert.Equal("abcdefgh", value);
    }
    
    [Fact]
    public void should_encode_medium_string_1()
    {
        // Arrange
        var byteBuffer = new ByteBuffer();

        // Act
        // Payload is small enough so we can use stackalloc optimization
        byteBuffer.WriteString("abcdefghijkl");

        // Assert
        var expected = new byte[] { 0, 0, 0, 12, 0, 12, 97, 98, 99, 100, 101, 102, 103, 104, 105, 106, 107, 108 };
        Assert.Equal(expected, byteBuffer.GetBuffer().ToArray());
    }
    
    [Fact]
    public void should_decode_medium_string_1()
    {
        // Arrange
        var byteBuffer = new ByteBuffer([0, 0, 0, 12, 0, 12, 97, 98, 99, 100, 101, 102, 103, 104, 105, 106, 107, 108]);

        // Act
        // Payload is small enough so we can use stackalloc optimization
        var value = byteBuffer.ReadString();

        // Assert
        Assert.Equal("abcdefghijkl", value);
    }
    
    [Fact]
    public void should_encode_medium_string_2()
    {
        // Arrange
        var byteBuffer = new ByteBuffer();

        // Act
        // Payload is large enough so we have to use ArrayPool instead of stackalloc
        var str = Enumerable.Repeat(0, 17).Aggregate("", (s, _) => s + "abcdefgh");
